import ExtendableError from 'es6-error';
import { formatLocation } from './formatting';

function formatError(message, location) {
  const locationStr = formatLocation(location);
  return `${location.file}:\n${locationStr}${locationStr ? ' ' : ''}${message}`;
}

export default class TemplateRenderError extends ExtendableError {
  constructor(message, location) {
    super(formatError(message, location));
  }

}