import moment from 'moment-timezone';
export default function createDateFilter({
  dates = {},
  locale: defaultLocale
}) {
  return (raw, options = {}) => {
    if (typeof options === 'string') {
      options = {
        format: options
      };
    }

    const {
      format = dates.format,
      tz = dates.tz,
      locale = defaultLocale
    } = options;
    const date = moment.utc(raw).tz(tz);
    date.locale(locale);
    return date.format(format);
  };
}