export function updateIn(obj, path, update) {
  const lastIndex = path.length - 1;
  let current = obj;

  for (let i = 0; i < path.length; i++) {
    const key = path[i];

    if (current[key] === undefined) {
      break;
    } else if (i === lastIndex) {
      current[key] = update(current[key]);
    } else {
      current = current[key];
    }
  }

  return obj;
}
export function getIn(obj, path, fallback) {
  let current = obj;

  for (const key of path) {
    if (current[key] === undefined) {
      return fallback;
    }

    current = current[key];
  }

  return current;
}